import React, {useEffect, useState} from 'react'

//axios
import axios from 'axios';

//Components
import Stats from "./Stats";

function Countires() {
    const [done, setDone] = useState(false);

    const [data, setData] = useState(null);

    useEffect(() => {
        axios.get('https://coronavirus-tracker-api.herokuapp.com/all')
            .then(response => {
                const stats = <Stats confirmed={response.data.confirmed.locations}
                                death={response.data.deaths.locations}
                                recovered={response.data.recovered.locations}/>

                setData(stats)
                setDone(true);
            })

    }, []);

    return (
        <div>
            {done
            ?
            data
            :
            null}
        </div>
    )
}

export default Countires
