import React, {useEffect, useState} from 'react'

import axios from 'axios';

function CoronaNumbers() {
    const [done, setDone] = useState(false);

    const [data, setData] = useState(null);

    useEffect(() => {
        axios.get('https://coronavirus-tracker-api.herokuapp.com/all')
            .then(response => {
                const allThree = response.data.latest;

                setData(allThree)
                setDone(true);
            })
    }, []);

    return (
        <div className='main'>
            {done ?
                <div className='corona-numbers'>
                    <ul>
                        <li>{data.confirmed}</li>
                        <li>{data.deaths}</li>
                        <li>{data.recovered}</li>
                    </ul>
                </div>
                :
                <h2>Loading...</h2>
            }

        </div>
    )
}

export default CoronaNumbers
