import React, {useEffect, useState} from 'react'

import axios from 'axios';

function CoronaNumbers() {
    const [done, setDone] = useState(false);

    const [data, setData] = useState(null);

    useEffect(() => {
        axios.get('https://coronavirus-tracker-api.herokuapp.com/all')
            .then(response => {
                const allThree = response.data.latest;

                setData(allThree)
                setDone(true);
            })
    }, []);

    const sick = {
        color: 'yellow;'
    }

    return (
        <div className='main'>
            {done ?
                <div className='corona-numbers'>
                    <ul>
                        <h3>Confirmed Cases:</h3>
                        <li>{data.confirmed}</li>

                        <h3>Total Deaths:</h3>
                        <li>{data.deaths}</li>

                        <h3>Recovered:</h3>
                        <li>{data.recovered}</li>
                    </ul>
                </div>
                :
                <h2>Loading...</h2>
            }

        </div>
    )
}

export default CoronaNumbers
