import React, { useState, useEffect } from 'react'

//axios
import axios from 'axios'

//Components
import Loader from "./Loader";

//Bootstrap
import Container from "react-bootstrap/Container";
import Card from "react-bootstrap/Card";

//Char.js
import { Line } from "react-chartjs-2";

function CoronaNumbersChart() {
    const [done, setDone] = useState(false);

    const [sick, setSick] = useState(null)
    const [dead, setDead] = useState(null)
    const [recovered, setRecovered] = useState(null)

    useEffect(() => {
        axios.get('https://coronavirus-tracker-api.herokuapp.com/all')
            .then(response => {
                setSick(response.data.confirmed.locations.map(country => {
                    if(country.latest !== 0) {
                        return country.latest;
                    }
                }));
                setDead(response.data.deaths.locations.map(country => {
                    if(country.latest !== 0) {
                        return country.latest;
                    }
                }));
                setRecovered(response.data.recovered.locations.map(country => {
                    if(country.latest !== 0) {
                        return country.latest;
                    }
                }));

                setDone(true);
            })
    }, []);

    console.log(sick);

