            }]
    }

    const deadData = {
        labels: dead,
        datasets: [
            {
                label: 'Dead',
                backgroundColor: '#ff6666',
                borderColor: '#ff4d4d',
                data: dead
            }]
    }

    const recoveredData = {
        labels: recovered,
        datasets: [{
            label: 'Recovered',
            backgroundColor: '#85e085',
            borderColor: '#70db70',
            data: recovered
        }]
    }*/

    return (
        <React.Fragment>
            {done
                ?
                <Container>
                    <div className='charts-main'>
                        {/*<Card>
                            <Line data={sickData} options={{responsive: true}}/>
                        </Card>
                        <Card>
                            <Line data={deadData} options={{responsive: true}}/>
                        </Card>
                        <Card>
                            <Line data={recoveredData} options={{responsive: true}}/>
                        </Card>*/}
                    </div>
                </Container>
                :
                <Loader/>
            }
        </React.Fragment>
    )
}

export default CoronaNumbersChart
