import React, {useEffect, useState} from 'react'

//axios
import axios from 'axios';

//Bootstrap
import Container from 'react-bootstrap/Container'
import Jumbotron from "react-bootstrap/Jumbotron";

function CoronaNumbers() {
    const [done, setDone] = useState(false);

    const [data, setData] = useState(null);

    useEffect(() => {
        axios.get('https://coronavirus-tracker-api.herokuapp.com/all')
            .then(response => {
                const allThree = response.data;


                setData(allThree);
                setDone(true);
            })
    }, []);

    const sick = {
        color: 'gray'
    }

    const dead = {
        color: 'red'
    }

    const recovered = {
        color: 'lightGreen'
    }

    return (
        <div className='main'>
                {done ?
                    <div className='corona-numbers'>
                        <div className='corona-date'>
                            <Jumbotron fluid={true}>
                                <p>This was last updated:</p>
                                <p>{data.confirmed.last_updated}</p>
                            </Jumbotron>
                        </div>
                        <ul>
                            <Jumbotron>
                                <h3>Confirmed Cases:</h3>
                                <li style={sick}>{data.latest.confirmed}</li>
                            </Jumbotron>

                            <Jumbotron>
                                <h3>Total Deaths:</h3>
                                <li style={dead}>{data.latest.deaths}</li>
                            </Jumbotron>

                            <Jumbotron>
                                <h3>Recovered:</h3>
                                <li style={recovered}>{data.latest.recovered}</li>
                            </Jumbotron>
                        </ul>
                    </div>
                    :
                    <h2>Loading...</h2>
                }
        </div>
    )
}

export default CoronaNumbers
