import React, { useState, useEffect } from 'react'

//axios
import axios from 'axios'

//Char.js
import { Line } from "react-chartjs-2";
import Loader from "./Loader";

function CoronaNumbersChart() {
    const [done, setDone] = useState(false);
    const [sick, setSick] = useState(null)
    const [dead, setDead] = useState(null)
    const [recovered, setRecovered] = useState(null)

    useEffect(() => {
        axios.get('https://coronavirus-tracker-api.herokuapp.com/all')
            .then(response => {
                setSick(response.data.confirmed.locations.map(country => country.latest))
                setDead(response.data.deaths.locations)
                setRecovered(response.data.recovered.locations)
            })

    }, []);

    console.log(sick);
    console.log(dead);
    console.log(recovered);

    return (
        <React.Fragment>
            {done
                ?
                <div className='charts-main'>
                    {/*<Line data={sick} options={{responsive: true}}/>
                    <Line data={dead} options={{responsive: true}}/>
                    <Line data={recovered} options={{responsive: true}}/>*/}
                </div>
                :
                <Loader/>
            }
        </React.Fragment>
    )
}

export default CoronaNumbersChart
