import React, { useState, useEffect } from 'react'

//axios
import axios from 'axios'

//Components
import Loader from "./Loader";

//Bootstrap
import Container from "react-bootstrap/Container";
import Card from "react-bootstrap/Card";

//Char.js
import { Line } from "react-chartjs-2";

function CoronaNumbersChart() {
    const [done, setDone] = useState(false);

    const [sick, setSick] = useState(null)
    const [dead, setDead] = useState(null)
    const [recovered, setRecovered] = useState(null)

    useEffect(() => {
        axios.get('https://coronavirus-tracker-api.herokuapp.com/all')
            .then(response => {
                setSick(response.data.confirmed.locations.filter(country => {
                    if(country.latest !== 0) {
                        return true;
                    }
                    return false;
                }));
                /*setDead(response.data.deaths.locations.map(country => country.latest));
                setRecovered(response.data.recovered.locations.map(country => country.latest));*/

                setDone(true);
            })
    }, []);

    console.log(sick);

    const sickData = {
        labels: sick,
        datasets: [
            {
                label: 'Sick',
                backgroundColor: '#cccccc', //Lighter
                borderColor: '#bfbfbf', //Darker
                data: sick
            }]
    }

    const deadData = {
        labels: dead,
        datasets: [
            {
                label: 'Dead',
                backgroundColor: '#ff6666',
                borderColor: '#ff4d4d',
                data: dead
            }]
    }

    const recoveredData = {
        labels: recovered,
        datasets: [{
            label: 'Recovered',
            backgroundColor: '#85e085',
            borderColor: '#70db70',
            data: recovered
        }]
    }

    return (
        <React.Fragment>
            {done
                ?
                <Container>
                    <div className='charts-main'>
                        {/*<Card>
                            <Line data={sickData} options={{responsive: true}}/>
                        </Card>
                        <Card>
                            <Line data={deadData} options={{responsive: true}}/>
                        </Card>
                        <Card>
                            <Line data={recoveredData} options={{responsive: true}}/>
                        </Card>*/}
                    </div>
                </Container>
                :
                <Loader/>
            }
        </React.Fragment>
    )
}

export default CoronaNumbersChart
