import React, {useEffect, useState} from 'react'

//axios
import axios from 'axios';

//Bootstrap
import Container from 'react-bootstrap/Container'

function CoronaNumbers() {
    const [done, setDone] = useState(false);

    const [data, setData] = useState(null);

    useEffect(() => {
        axios.get('https://coronavirus-tracker-api.herokuapp.com/all')
            .then(response => {
                const allThree = response.data;


                setData(allThree);
                setDone(true);
            })

    }, []);

    function countThemUp() {
        let confirmed = 0;
        let deaths = 0;
        let recovered = 0;

        while(confirmed < data.latest.confirmed &&
            deaths < data.latest.deaths &&
            recovered < data.latest.recovered) {

        }
    }

    return (
        <div className='main'>
            {done ?
                <div className='corona'>
                    <div className='corona-date'>
                        <Container>
                            <h3>This was last updated:</h3>
                            <h3>{data.confirmed.last_updated}</h3>
                        </Container>
                    </div>
                    <ul>
                        <div className='corona-numbers'>
                            <Container>
                                <h3>Confirmed Cases:</h3>
                                <li>{data.latest.confirmed}</li>

                                <h3>Total Deaths:</h3>
                                <li>{data.latest.deaths}</li>

                                <h3>Recovered:</h3>
                                <li>{data.latest.recovered}</li>
                            </Container>
                        </div>
                    </ul>
                </div>
                :
                <h2>Loading...</h2>
            }
        </div>
    )
}

export default CoronaNumbers
