//Bootstrap
import Container from "react-bootstrap/Container";

function Stats(props) {
    const [countries, setCountires] = useState(null);

    useEffect(() => {
        setCountires()

    }, [])

    const confirmed = props.confirmed.map(location =>
    <div>
        <h3>{location.country}</h3>
        <p>{location.latest}</p>
    </div>)

    return (
        <div className='stats'>
            <Container>
                {countries}
                <hr/>
            </Container>
        </div>
    )
}

export default  Stats
