import React, {useEffect, useState} from 'react'

//axios
import axios from 'axios';
import countUp from 'countup.js'

//Bootstrap
import Container from 'react-bootstrap/Container'

function CoronaNumbers() {
    const [done, setDone] = useState(false);

    const [data, setData] = useState(null);

    useEffect(() => {
        axios.get('https://coronavirus-tracker-api.herokuapp.com/all')
            .then(response => {
                const allThree = response.data;

                setData(allThree);
                setDone(true);
            })

        if(setDone) {
            const countUp = new countUp(' ', data.latest.deaths)

        }

    }, []);

    return (
        <div>
            {done ?
                <div className='corona'>
                    <div className='corona-date'>
                        <Container>
                            <h3>This was last updated:</h3>
                            <h3>{data.confirmed.last_updated}</h3>
                        </Container>
                    </div>
                        <div className='corona-numbers'>
                            <Container>
                                <ul>
                                    <h3>Confirmed Cases:</h3>
                                    <li>
                                        <h2 className='latest'>{data.latest.confirmed}</h2>
                                    </li>
                                    <hr/>
                                    <h3>Total Deaths:</h3>
                                    <li>
                                        <h2 className='death'>{data.latest.deaths}</h2>
                                    </li>
                                    <hr/>
                                    <h3>Recovered:</h3>
                                    <li>
                                        <h2 className='recovered'>{data.latest.recovered}</h2>
                                    </li>
                                    <hr/>
                                </ul>
                            </Container>
                        </div>
                </div>
                :
                <h2>Loading...</h2>
            }
        </div>
    )
}

export default CoronaNumbers
